import React from "react";
import actions from "../state/actions";
import VideoThumbnail from "./VideoThumbnail";
import styled from "styled-components";

const Wrapper = styled.div`
  display: flex;
  flex-direction: column;
  width: 200px;
  height: 100vh;
  border-top-left-radius: 10px;
  border-bottom-left-radius: 10px;
  overflow: hidden;
  background-color: white;
  border: 3px solid #f3ca20;
  padding: 10px;

  :hover {
    overflow-y: scroll;
  }
`;

const Hthree = styled.h3`
  background-color: #f3ca20;
  color: black;
  padding: 10px;
`;

export default function VideoList({ dispatch, videos }) {
  const sorted = videos.sort(function (a, b) {
    let textA = a.name.toUpperCase();
    let textB = b.name.toUpperCase();
    return textA < textB ? -1 : textA > textB ? 1 : 0;
  });

  return (
    <Wrapper>
      <Hthree>Up Next : </Hthree>
      {sorted.map((video, i) => {
        return (
          <VideoThumbnail
            dispatch={dispatch}
            key={`${video.name}-thumbnail`}
            name={video.name}
            onClick={() => {
              dispatch({ type: actions.SELECT_VIDEO, payload: i });
            }}
            src={video.thumbnail}
          />
        );
      })}
    </Wrapper>
  );
}
